import Layout from '../components/MyLayout';
import { useSelector, RootStateOrAny, useDispatch } from 'react-redux';
import Link from 'next/link';
import { NextPage } from 'next';
import {
  getPostFromServer,
  getPostsFromServer,
  removePostFromServer,
} from '../api';
import { setPostsAction, setPost } from '../store/actions';
import { Post } from '../types';
import { useEffect } from 'react';

const Index: NextPage = () => {
  const dispatch = useDispatch();
  const posts = useSelector((state: RootStateOrAny) => state.posts.posts);

  const getPosts = async () => {
    dispatch(setPostsAction(await getPostsFromServer()));
  };
  const removePost = async (id: number | undefined) => {
    await removePostFromServer(id);
    getPosts();
  };

  useEffect(() => {
    getPosts();
  }, []);

  return (
    <Layout>
      <h1>Posts</h1>
      <ul>
        {posts.map((post: Post) => (
          <li key={post.id}>
            <div>
              <span onClick={() => removePost(post.id)}>{`delete`}</span>
              <Link href="/posts/[id]" as={`/posts/${post.id}`}>
                <a
                  onClick={async () =>
                    dispatch(setPost(await getPostFromServer(post.id)))
                  }
                >
                  {post.title}
                </a>
              </Link>
              {post.body}
            </div>
          </li>
        ))}
      </ul>
    </Layout>
  );
};

Index.getInitialProps = async function({ store }) {
  const data = await getPostsFromServer();
  store.dispatch(setPostsAction(data));
console.log(data);
  return {
    posts: data,
  };
};

export default Index;
