import axios from 'axios';
import { setPosts } from './store/actions';
export const baseURL = 'https://simple-blog-api.crew.red';
export const api = axios.create({
  baseURL,
  timeout: 1000,
  headers: {
    'Content-Type': 'application/json; charset=utf-8'
  }
});
export const getPostFromServer = async postId => {
  const data = await api.get(baseURL + '/posts/' + postId + '?_embed=comments').then(({
    data
  }) => data);
  return data;
};
export const sendCommentOnServer = async (postId, commentBody) => {
  await api.post(baseURL + '/comments', {
    postId,
    body: commentBody
  });
};
export const sendPostOnServer = async (title, body) => {
  await api.post(baseURL + '/posts', {
    title,
    body
  });
};
export const getPostsFromServer = async () => {
  const data = await api.get(baseURL + '/posts').then(({
    data
  }) => data);
  return data;
};
export const removePostFromServer = async id => {
  await api.delete(baseURL + '/posts/' + id);
};
export const test = () => dispatch => api.get(baseURL + '/posts').then(({
  data
}) => data).then(posts => dispatch(setPosts(posts)));
