import { SET_POSTS, SET_POST, SET_POST_BODY, SET_POST_TITLE, SET_COMMENT, SET_COMMENT_BODY } from '../types';
export const setCommentBody = commentBody => ({
  type: SET_COMMENT_BODY,
  commentBody
});
export const setComment = comment => ({
  type: SET_COMMENT,
  comment
});
export const setPosts = posts => ({
  type: SET_POSTS,
  posts
});
export const setPost = post => ({
  type: SET_POST,
  post
});
export const setPostBody = postBody => ({
  type: SET_POST_BODY,
  postBody
});
