import Layout from '../components/MyLayout';
import { useSelector, RootStateOrAny, useDispatch } from 'react-redux';
import Link from 'next/link';
import { NextPage } from 'next';
import {
  getPostFromServer,
  getPostsFromServer,
  removePostFromServer,
} from '../api';
import { setPosts, setPost } from '../store/actions';
import { Post } from '../types';
import { makeStyles, createStyles, Theme } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import { NoSsr, Button } from '@material-ui/core';

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    postBody: {
      maxWidth: '80%',
      margin: 'auto',
      marginTop: '10px',
      textAlign: 'justify',
    },
    link: {
      display: 'block',
      textDecoration: 'none',
      color: 'black',
      fontSize: '24px',
    },
    root: {
      flexGrow: 1,
    },
    paper: {
      marginTop: '20px',
      padding: theme.spacing(2),
      textAlign: 'center',
      color: theme.palette.text.primary,
      outline: 'none',
    },
  }),
);

const Index: NextPage = () => {
  const classes = useStyles();

  const dispatch = useDispatch();
  const posts = useSelector((state: RootStateOrAny) => state.posts.posts);

  const getPosts = async () => {
    dispatch(setPosts(await getPostsFromServer()));
  };
  const removePost = async (id: number | undefined) => {
    await removePostFromServer(id);
    getPosts();
  };

  return (
    <Layout>
      <NoSsr>
        <div className={classes.root}>
          <Grid container spacing={3}>
            {posts.map((post: Post) => (
              <Grid item xs={12} sm={6} key={post.id}>
                <Paper 
                className={classes.paper}>
                  <Link href="/posts/[id]" as={`/posts/${post.id}`}>
                    <a
                      className={classes.link}
                    >
                      {post.title}
                    </a>
                  </Link>
                  <div className={classes.postBody}>{post.body}</div>
                  <Button
                    onClick={() => removePost(post.id)}
                  >{`delete`}</Button>
                </Paper>
              </Grid>
            ))}
          </Grid>
        </div>
      </NoSsr>
    </Layout>
  );
};

Index.getInitialProps = async ({ store }) => {
  const data = await getPostsFromServer();
  store.dispatch(setPosts(data));
  return { data };
};

export default Index;

