import { Actions, Comment, SET_COMMENT } from '../types';

const initialState = {
  comment: {} as Comment,
};

export const commentReducer = (state = initialState, action: Actions) => {
  switch (action.type) {
    case SET_COMMENT:
      return { ...state, comment: action.comment };
    default:
      return state;
  }
};
